update.foreach:
update 
set account_balance = account_balance + #{item.amount},update_time = now(),version = version + 1
where account_code = #{item.accountCode,jdbcType=BIGINT}
and mer_account_type_code = #{item.merAccountTypeCode}

mapper.update:
UPDATE  set account_balance = account_balance + #{accountBalance}, update_user = #{updateUser},update_time = now(),version = version + 1
where account_code = #{accountCode}
and account_balance + #{accountBalance} >= 0
and mer_account_type_code = #{merAccountTypeCode}

UPDATE  set account_balance = #{accountBalance}, update_user = #{updateUser},update_time = now(),version = version + 1
where account_code = #{accountCode}
and mer_account_type_code = #{merAccountTypeCode}

mapper.select:
select count(*)
from account_amount_type t
    inner join account a on a.account_code = t.account_code
where a.mer_code = #{merCode}
  and t.mer_account_type_code = #{merAccountTypeCode}
