mapper.select:
select t1.id as accountDeductionAmountId, oi.order_id as orderId, t1.trans_no as transNo, t1.account_code as accountCode, t2.account_name as accountName, t1.card_id as cardId, t3.mer_code as merCode, t3.mer_name as merName, t1.store_code as storeCode, t4.store_alias as storeName, t1.trans_time as transTime, t1.pos as pos, t1.pay_code as payCode, '员工卡支付' as payName, t1.trans_type as transType, case when t1.trans_type='consume' then '消费' when t1.trans_type='refund' then '退款' end as transTypeName, t1.trans_amount as transAmount, t1.mer_account_type as merAccountType, t5.mer_account_type_name as merAccountTypeName, t1.account_deduction_amount as accountAmount, t1.account_amount_type_balance as accountBalance, t1.mer_deduction_amount as merDeductionAmount, t1.mer_deduction_credit_amount as merCreditDeductionAmount, t1.order_channel as orderChannel, 'unsettled' as settleFlag, 'system' as create_user, case when t2.mer_code = ss.mer_code then 'self' else 'third' end as storeType, mc.current_balance as merBalance, mc.remaining_limit as merCredit from account_deduction_detail t1 left join account t2 on t1.account_code=t2.account_code left join merchant t3 on t2.mer_code=t3.mer_code left join merchant_credit mc on mc.mer_code=t3.mer_code left join merchant_store_relation t4 on t1.store_code = t4.store_code and t2.mer_code=t4.mer_code and t4.deleted=0 left join merchant_account_type t5 on t2.mer_code = t5.mer_code and t1.mer_account_type=t5.mer_account_type_code left join order_info oi on oi.trans_no = t1.trans_no left join supplier_store ss on ss.store_code = t1.store_code
t1.id >=#{minId}
and t1.trans_time >=#{startTime,jdbcType=TIMESTAMP}
and t1.trans_time <=#{endTime,jdbcType=TIMESTAMP}
and t1.trans_type in('consume','refund') and t1.mer_account_type in ('surplus_quota','surplus_quota_overpay')
group by t1.id order by t1.id
limit ${limit}

select esd.id id, esd.trans_no transNo, esd.order_id orderId, esd.trans_time transTime, esd.mer_account_type_name merAccountType, esd.account_name accountName, a.phone phone, a.store_code departmentCode, d.department_name departmentName, esd.store_code storeCode, esd.store_name storeName, case when esd.store_type = 'self' then '自营' when esd.store_type = 'third' then '第三方' end as storeType, case when esd.trans_type='consume' then IFNULL(esd.trans_amount,0) when esd.trans_type='refund' then IFNULL(esd.trans_amount*-1,0) else 0 end transAmount, if(esd.mer_account_type = 'surplus_quota_overpay' and esd.trans_type = 'refund', 0, esd.trans_amount) as settleAmount, case when esd.settle_flag = 'unsettled' then '待结算' when esd.settle_flag = 'settling' then '结算中' when esd.settle_flag = 'settled' then '已结算' end as settleFlag from employee_settle_detail esd left join account a on esd.account_code = a.account_code left join department d on a.store_code=d.department_code where 1=1
order by esd.id limit #{query.limit}
order by esd.trans_time desc

select
esd.account_code as accountCode,
a.account_name as accountName,
a.phone as phone,
d.department_name as departmentName,
count(esd.trans_amount) as orderNum,
sum(
case when esd.mer_account_type ='surplus_quota_overpay' then 0
else esd.trans_amount end
) as totalSettleAmount,
sum(esd.trans_amount) as totalConsumerAmount,
min(esd.trans_time) as minTransTime,
max(esd.trans_time) as maxTransTime
from employee_settle_detail esd
left join account a on esd.account_code = a.account_code
left join department d on a.store_code = d.department_code

    esd.settle_flag='unsettled'

        and a.account_name like concat('%',#{query.accountName},'%')


        and a.phone =#{query.phone}


        and

            d.department_path like CONCAT(#{item}, '%')



        and esd.order_id = #{query.orderId}


        and esd.trans_no=#{query.transNo}


        and esd.store_type =#{query.storeType}


        and esd.trans_time >=#{query.transTimeStart,jdbcType=TIMESTAMP}


        and esd.trans_time <=#{query.transTimeEnd,jdbcType=TIMESTAMP}


        and esd.mer_account_type=#{query.merAccountType}


        and a.account_code in

            #{item}



group by esd.account_code

