mapper.select:
select * from (SELECT m.id id, m.mer_name merName, m.mer_code merCode, 0 departmentParent, m.mer_type merType, m.mer_identity merIdentity, m.mer_cooperation_mode merCooperationMode, mc.credit_limit creditLimit, mc.remaining_limit remainingLimit, mc.current_balance currentBalance, m.create_time createTime FROM merchant m LEFT JOIN merchant_credit mc ON m.mer_code = mc.mer_code and mc.deleted=0 and m.deleted=0 ORDER BY m.create_time asc) temp0
and temp0.departmentCode=#{req.merCode}
and temp0.departmentName like concat('%',#{req.merCode},'%')
and temp0.merType=#{req.merType}
and temp0.merCooperationMode=#{req.merCooperationMode}
and temp0.createTime >= #{req.startTime}
and temp0.createTime <= #{req.endTime}
UNION select * from (SELECT d.id id, d.department_name merName, d.department_code merCode, d.department_parent departmentParent, d.department_type merType, NULL merIdentity, NULL merCooperationMode, NULL creditLimit, NULL remainingLimit, NULL currentBalance, d.create_time createTime FROM department d where d.deleted=0 ORDER BY d.create_time asc) temp
and temp.departmentCode=#{req.merCode}
and temp.departmentName like concat('%',#{req.merCode},'%')
and temp.merType=#{req.merType}
and temp.merCooperationMode=#{req.merCooperationMode}
and temp.createTime >= #{req.startTime}
and temp.createTime <= #{req.endTime}
